const attendanceGraph=document.getElementById("attendance-graph");function generateHalfHourLabels(e=8,t=22){const n=[];for(let a=e;a<=t;a++)n.push(`${a}:00`),a!==t&&n.push(`${a}:30`);return n}const hours=generateHalfHourLabels();function getRandomHeight(){return Math.floor(55*Math.random())+12}function getColorByHeight(e){return e<=29?"#ffc59b":e<=49?"#f88c6c":"#e64e21"}function renderGraph(){attendanceGraph.innerHTML="",hours.forEach((e=>{const t=getRandomHeight(),n=getColorByHeight(t),a=document.createElement("div");a.classList.add("bar-group");const r=document.createElement("div");r.classList.add("bar"),r.dataset.hour=e,r.style.height=`${t}px`,r.style.backgroundColor=n;const o=document.createElement("div");o.classList.add("bar-label"),e.endsWith(":00")&&(o.textContent=e),a.appendChild(r),a.appendChild(o),attendanceGraph.appendChild(a)}))}renderGraph(),setInterval((()=>{renderGraph()}),3e4);const swiper=new Swiper(".swiper",{loop:!0,slidesPerView:1,centeredSlides:!0,autoplay:{delay:5e3,disableOnInteraction:!1},navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"}});